#设置客户端链接服务器端的编码为utf-8
set names utf8;
drop database if exists menu;
#创建新的数据库，设置存储的编码为UTF-8
create database menu charset=utf8;
use menu;
#用户表
create table menu_user(
  id int unsigned NOT NULL AUTO_INCREMENT COMMENT '用户id，主键且自增',
  username varchar(30) NOT NULL COMMENT '用户名,唯一',
  password varchar(32) NOT NULL COMMENT '密码,MD5',
  nickname varchar(30) DEFAULT NULL,
  avatar varchar(50) NOT NULL DEFAULT 'unnamed.jpg' COMMENT '用户头像',
  article_number mediumint(9) NOT NULL DEFAULT '0' COMMENT '发表的文章数量',
  PRIMARY KEY(`id`),
  UNIQUE KEY `username`(`username`)
);
#菜单详情表1
create table menu_article(
  id int unsigned NOT NULL AUTO_INCREMENT,
  subject varchar(50) NOT NULL COMMENT '菜名',
  main varchar(255) NOT NULL COMMENT '主料',
  content text NOT NULL COMMENT '做菜步骤', 
  image varchar(50) DEFAULT NULL COMMENT '缩略图',
  user_id int unsigned NOT NULL COMMENT '外键，用户id',
  category_id int unsigned NOT NULL COMMENT '外键，分类id',
  PRIMARY KEY(`id`)
);
'秋葵炒虾仁', '主料:虾200克,黄秋葵200克 辅料:大葱1根,生姜1块,白胡椒粉1勺,大蒜3瓣,盐3克,姜汁酒1勺,生抽1勺', '1.大蒜切片，大葱切段，生姜切末。2.在虾肉中加入一大勺姜汁酒和少许白胡椒粉抓拌均匀。3.秋葵洗净后去蒂改刀成段。4.锅内放少许橄榄油，下入葱姜蒜末爆香。5.下入腌好的虾仁翻炒。6.炒至虾仁变色后调入一勺生抽。7.下入秋葵快速翻炒。8.调入2克盐。9.出锅前淋入一小勺姜汁酒爆香后出锅即可。', 'img1.png', '1', '3'
);
#菜单详情表2
create table home_article(
  id int unsigned NOT NULL AUTO_INCREMENT,
  subject varchar(50) NOT NULL COMMENT '菜名',
  main varchar(255) NOT NULL COMMENT '主料',
  content text NOT NULL COMMENT '做菜步骤', 
  image varchar(50) DEFAULT NULL COMMENT '缩略图',
  user_id int unsigned NOT NULL COMMENT '外键，用户id',
  home_id int unsigned NOT NULL COMMENT '外键，分类id',
  PRIMARY KEY(`id`)
);
