#设置客户端链接服务器端的编码为utf-8
set names utf8;
drop database if exists menu;
#创建新的数据库，设置存储的编码为UTF-8
create database menu charset=utf8;
use menu;
#用户表
create table menu_user(
  id int unsigned NOT NULL AUTO_INCREMENT COMMENT '用户id，主键且自增',
  username varchar(30) NOT NULL COMMENT '用户名,唯一',
  password varchar(32) NOT NULL COMMENT '密码,MD5',
  nickname varchar(30) DEFAULT NULL,
  avatar varchar(50) NOT NULL DEFAULT 'unnamed.jpg' COMMENT '用户头像',
  article_number mediumint(9) NOT NULL DEFAULT '0' COMMENT '发表的文章数量',
  PRIMARY KEY(`id`),
  UNIQUE KEY `username`(`username`)
);
insert into menu_user values('1','刘惠平','111111','lhp','1.jpg','0'),
                            ('2','张怡','222222','zy','2.jpg','0'),
                            ('3','李玉洁','333333','lyj','3.jpg','0');
#菜单详情表
create table menu_article(
  id int unsigned NOT NULL AUTO_INCREMENT,
  subject varchar(50) NOT NULL COMMENT '菜名',
  main varchar(255) NOT NULL COMMENT '主料',
  content text NOT NULL COMMENT '做菜步骤', 
  image varchar(50) DEFAULT NULL COMMENT '缩略图',
  category_id int unsigned NOT NULL COMMENT '外键，分类id',
  user_id int unsigned NOT NULL COMMENT '外键，用户id',
  PRIMARY KEY(`id`)
);
insert into menu_article values('1',
'秋葵炒虾仁', '主料:虾200克,黄秋葵200克 辅料:大葱1根,生姜1块,白胡椒粉1勺,大蒜3瓣,盐3克,姜汁酒1勺,生抽1勺', '1.大蒜切片，大葱切段，生姜切末。2.在虾肉中加入一大勺姜汁酒和少许白胡椒粉抓拌均匀。3.秋葵洗净后去蒂改刀成段。4.锅内放少许橄榄油，下入葱姜蒜末爆香。5.下入腌好的虾仁翻炒。6.炒至虾仁变色后调入一勺生抽。7.下入秋葵快速翻炒。8.调入2克盐。9.出锅前淋入一小勺姜汁酒爆香后出锅即可。', '/articleimages/img1.png', '1', '3'
);
insert into menu_article values('2', '豆腐鲫鱼汤', '主料:姜片适量,鲫鱼1条,葱段适量,嫩豆腐250克 辅料:油适量,水750毫升,料酒3克,胡椒粉1克,鸡粉5克,盐3克', '1.锅中倒油，翻入姜片，放入鲫鱼，小火慢煎，煎至金黄后翻面。2.锅中倒750ml水煮沸，倒入煎好的鱼。3.加入姜片、葱段、料酒3g、胡椒粉1g、鸡粉5g、盐3g、嫩豆腐250g。4.大火煮沸转至中火，加盖炖煮15分钟。5.出锅，完成。', '/articleimages/img2.png', '2', '4');
insert into menu_article values('3', '萝卜排骨汤', '主料:排骨860克,红萝卜780克 辅料:葱叶适量,姜片6片,料酒适量,食盐4小勺,枸杞少许', '1.排骨焯水捞出，用温水洗净浮沫。2.煮锅置火上，放足量的水，放入焯过水的排骨，加入葱段姜片。3.大火烧沸后，盖上盖子改中小炖煮4.煮排骨的时候处理白萝卜，白萝卜去皮切成滚刀块。5.排骨大约煮40钟，放入切好的白萝卜，继续中小火煮制。6.煮至白萝卜熟透，加入适量食盐、胡椒粉即可关火。', '/articleimages/img3.png', '3', '4');
insert into menu_article values('4', '鸡腿炖土豆', '主料:鸡腿5个,土豆200克 辅料:食盐3克,大葱1根,水适量,姜片3片,花椒8颗,老抽5毫升,冰糖3块,料酒1勺,小尖椒2个', '1.准备好鸡腿，土豆和辅料，土豆切成土豆块，鸡腿热水焯一下。2.锅中放油，将冰糖放进油中融化。3.冰糖完全融化。4.鸡腿倒进冰糖中翻炒，直至完全上色。5.将辅料倒进锅中翻炒，加入老抽和料酒。6.大火翻炒，直至鸡腿上色均匀。7.锅中加入热水煮沸，再关小火炖1个小时。8.鸡腿炖熟后加入土豆，放入盐，等土豆熟了就可以出锅了。', '/articleimages/img4.png', '2', '7');
insert into menu_article values('5', '啤酒五花肉', '主料:五花肉1块,啤酒1罐,鸡蛋2个,葱适量 辅料:姜适量,料酒适量,八角适量,干辣椒适量,老抽1勺,盐2勺', '1.五花肉切小块。2.冷水下锅，放入葱、姜、料酒去腥，捞出后凉水洗净，鸡蛋煮熟剥皮开小口。3.锅中无油无水，放入五花肉煸出油。4.锅中放一勺蜂蜜，炒至焦糖色，放八角、姜片、干辣椒、一勺老抽，放五花肉炒香。5.倒入1罐啤酒，加1勺盐，放入鸡蛋，大火慢炖至收汁，最后盛出撒上葱花即可。', '/articleimages/img5.png', '3', '1');
insert into menu_article values('6', '红萝卜玉米排骨汤', '主料:肋排500克,胡萝卜1根,甜玉米1根 辅料:盐适量,香葱少许,清水适量', '1.准备好排骨洗净。2.锅加水，放入排骨。3.焯水煮一下。4.捞出排骨。5.准备好萝卜和玉米。6.准备点红枣和姜。7.玉米和萝卜洗净切块。8.锅加水，放入排骨，红枣和姜片。9.放入玉米和萝卜。10.煲煮50分钟，加盐调味。11.盛碗。', '/articleimages/img6.png','1', '4');
insert into menu_article values('7', '风林茄子', '主料:茄子250克 辅料:蒜5克,香菜1根,干辣椒2克,花椒1克,葱15克,白砂糖50克,陈醋40克,生抽5克,盐2克,淀粉120克', '1.蒜切末，香菜切段，茄子切块。2.倒入茄子，加入淀粉，搅拌均匀。3.锅中倒油，油温烧至五成熟加入茄子，炸至金黄。4.锅中倒油，加入花椒，干辣椒，葱段，大火爆香。5.加入陈醋，白砂糖，生抽，盐翻炒均匀。6.加入茄子，香菜，翻炒均匀。', '/articleimages/img7.png', '3', '6');
insert into menu_article values('8', '香辣排骨', '主料:小肋排800克 辅料:盐适量,玉米淀粉适量,料酒适量,生抽适量,葱姜蒜适量,绵白糖适量,黑胡椒碎适量,干红辣椒6个,花生油适量,去皮熟花生米适量', '1.排骨清水煮开、洗干净。2.准备一些姜和蒜。3.油热煸炒一下。4.然后下排骨炒一下。5.把辣椒放进去继续炒。6.下料酒、老抽、生抽适量！翻炒一下。7.加水没过排骨。8.煮开之后加入盐味精和白糖调味、转中火炖着、大概二十分钟左右。9.汤快干的时候把葱白放进去、放得早的话会煮烂掉、所以这时候正好。10.小火收汁、翻炒过后颜色已经很好看了。11.出锅后撒一点香菜和葱、非常的香。喜欢吃辣的话辣椒可以多放。', '/articleimages/img8.png', '2', '1');
insert into menu_article values('9', '辣子鸡', '主料:鸡腿500克,辣椒酱500克 辅料:姜1小块,大葱1段,盐适量', '1.放入鸡肉、加料酒、生抽、胡椒粉、盐，搅拌均匀，腌制30分钟。2.倒入食用油、姜丝、花椒，鸡块。3.小火煎至鸡肉变色。4.倒入大蒜、干辣椒、辣椒圈。5.翻炒均匀。', '/articleimages/img9.png', '2', '7');
insert into menu_article values('10', '豆角茄子煲', '主料：豆角200g，茄子200g，猪肉50g 调料：食盐适量，葱适量，姜适量，蒜适量，料酒适量，蚝油1勺，甜面酱2勺，调和油适量，花椒粉适量', '1.大茄子去皮切条，撒少量盐抓匀腌制片刻，炒之前去掉水分。2.豆腐煎至两面金黄，摆入浅沙煲底层。3.留底油炒熟茄子，盛到豆腐上。4.热油炒香葱姜蒜末，加入肉末炒至变色。5.加入豆瓣酱炒出红油，翻炒均匀，加入生抽、蚝油、白糖、十三香。6.翻炒均匀倒入青红椒碎，倒入适量水淀粉，翻炒均匀。7.浇在豆腐茄子上，盖盖焖三分钟，即可开吃。', '/articleimages/img10.png', '1', '6');
insert into menu_article values('11', '麻婆豆腐', '主料：豆腐500克 辅料：油一勺，盐一撮，肉末150克，豆瓣酱30克，花椒8克，葱姜末少许', '1.卤水豆腐洗净，切约1.5cm见方小块，盐水浸泡备用。2.大蒜、生姜，大葱切末，郫县豆瓣剁碎，蒜苗切末。我这次偷懒用的是牛肉酱，有点牛肉味即可。3.油烧7分热，下葱姜蒜末炒出香味，下牛肉酱划开炒匀，下郫县豆瓣满满炒出红油，加白糖调味。4.豆腐沥水下锅，炒匀，加入鸡汤，大火烧开盖上盖转中小火烧2-3分钟。淀粉加少许水调匀。5.下水淀粉勾芡，撒花椒粉、青蒜末炒匀，完成。', '/articleimages/img11.png', '1', '6');
insert into menu_article values('12', '土豆烧牛肉', '主料：牛肉500克，土豆1个 辅料：葱段半根，姜3片，蒜2粒，干红辣椒3个，八角1个，老抽一汤匙，生抽1汤匙，盐少许，砂糖少许', '1.将主料 辅料所以材料切好备用。2.切好的牛肉冷水锅加料酒炒掉血水。3.土豆切块 焯水的牛肉 盛盘备用。4.起锅热油 将白砂糖放入锅中炒出颜色。5.糖炒出颜色倒入牛肉 生抽 老抽 翻炒至颜色均匀后加入葱 姜 蒜 八角 辣椒 继续翻炒出香味。6.炒好的牛肉倒入高压锅或者电压力锅中添水 定时30分钟 即可。7.将准备好的土豆块 炖好的牛肉 倒入炖锅中 加少许盐 慢火炖15分钟左右 这一步是为了让土豆和牛肉的味道融合。8.炖好的牛肉盛盘即可食用。', '/articleimages/img12.png', '1', '1');
insert into menu_article values('13', '乌鸡汤', '主料：乌鸡1只 辅料：姜5克，葱5克，料酒5克，桂圆2个，红枣2个，黄芪3克，白芍2克，党参3克，当归2克，枸杞5克，盐2克', '1.乌鸡剁成块。2.冷水下锅，焯水，打去浮沫，倒入高压锅。3.放入姜，红枣，党参，盐，水，高压锅上气5分钟。4.泡点枸杞，煮好倒入铁盆，放入枸杞，切点葱花，开火煮，碗中放入葱花，煮好盛入碗中。', '/articleimages/img13.png', '2', '4');
insert into menu_article values('14', '西芹百合炒腰果', '主料：百合50克，西芹100克，腰果60克 辅料：盐5克，鸡粉3克，植物油2汤匙', '1.主料准备好；百合去头尾，掰成瓣，西芹切小片。2.将西芹和百合焯水后迅速捞起，泡在冷水中备用；锅里放适量油，烧至温热后放入腰果。3.用小火煎炸至腰果金黄酥脆后捞出，放凉备用，锅里留油；将西芹百合捞出控干，放入油锅里大火翻炒30秒左右。4.放入放凉后的腰果；调入适量盐和鸡粉即可关火出锅。', '/articleimages/img14.png', '3', '6');
insert into menu_article values('15', '西红柿炒鸡蛋', '主料：西红柿1个，鸡蛋3个 辅料：植物油10克，食盐3克，小葱两棵', '1.将鸡蛋入碗中，用筷子搅散。番茄洗净去蒂，切成小块备用。2.锅中放油烧至七成热时，将蛋液倒入锅中，在锅中快速搅打炒成蓬松的炒鸡蛋并盛出。3.锅中重新放油烧至五成热，先将葱花放入爆香。4.将番茄块倒入，大火翻炒1分钟，放入盐、鸡精、糖调味，搅拌均匀；加入鸡精让这道番茄炒蛋滋味更加鲜美，让人食欲大开。5.最后放入炒好的鸡蛋，炒匀即可盛出。', '/articleimages/img15.png', '2', '6');
insert into menu_article values('16', '栗子红烧肉', '主料：五花肉250克，栗子(鲜)250克 辅料：姜10克，蒜50克，生抽30毫升，老抽15毫升，料酒15毫升，细砂糖20克，食盐适量，植物油适量', '1.五花肉和冷水一起放进煮锅里，焯水，冲洗掉油脂，再把五花肉切块。2.炒锅放入1汤匙植物油，加入冰糖，用小火熔化。3.加入五花肉块。4.将五花肉块裹上琥珀色冰糖。5.加入生抽、老抽和料酒，炒匀。6.再放入姜和蒜，炒出香味。\r7.加入没过五花肉的开水，大火烧开后转小火焖60分钟。8.五花肉焖煮期间，当汤汁剩下三分之一时加入栗子，拌匀后继续焖。9.汤汁所剩无几时盛起，撒上葱粒即可。', '/articleimages/img16.png', '2', '1');
insert into menu_article values('17', '洋葱炒牛肉', '主料：牛肉500克，胡萝卜100克，洋葱100克 辅料：盐1克，料酒20毫升，胡椒粉1克，鸡蛋清20克，淀粉30克，亚麻籽油20毫升，姜蒜30克，生抽20毫升，香葱20克', '1.准备蒜末5g，洋葱100g切块，猪瘦肉200g切片。2.碗中倒入猪肉片，倒入生抽5g，淀粉5g、倒入蒜末拌匀，腌制20分钟。3.起锅放入油5ml，倒入猪肉片翻炒至变色。4.倒入老抽1g，生抽3g，翻炒均匀后倒入洋葱。5.加入盐1g，翻炒均匀即可出锅。', '/articleimages/img17.png', '2', '1');
insert into menu_article values('18', '水煮鱼', '主料：鸡蛋1个，草鱼片500克，姜片适量，黄豆芽100克 辅料：淀粉5克，食用油5毫升，郫县豆瓣酱15克，水500毫升\r', '1.打一个蛋清，准备草鱼片500g，倒入蛋清，加入淀粉5g，搅拌均匀。2.锅中热油5ml，倒入适量姜片，郫县豆瓣酱15g，炒出红油。3.锅中倒入500ml水，倒入100g黄豆芽，大火煮沸。4.倒入鱼片，继续煮。5.煮沸后出锅。\r', '/articleimages/img18.png', '2', '3');
insert into menu_article values('19', '蛋焖排骨', '主料：小肋排 1000克 ，鸡蛋 10个 辅料：葱 姜 酱油 糖 料酒 盐', '1.先煮好几个白水蛋待凉透剥去壳备用；排骨，姜片，干辣椒，冰糖。2.锅里放油，油温稍稍起来就放冰糖和姜片，小火，待冰糖化掉成焦糖，放入排骨翻炒，均匀的裹上糖色。3.然后放入适量酱油，料酒，干辣椒，加入水（水刚没过排骨即可）大火烧开，中火烧20分钟。4.然后鸡蛋上划几个口子，便于入味，放入锅中一起烧，不用盖锅盖。5.待汤汁收干，即可起锅；如果觉得味道不够，可以根据自己口味再适量加盐和鸡精。', '/articleimages/img19.png', '2', '1');
insert into menu_article values('20', '咖喱鸡块', '主料：鸡胸肉300克，红皮洋葱300克，土豆300克，胡萝卜100克，青椒50克 辅料：咖喱块50克，料酒15克，玉米油30克，水400毫升', '1.鸡胸肉清洗干净，放冰箱冷冻半小时变硬些，切成2cm见方的小块；洋葱、土豆、胡萝卜和青椒分别洗净切2cm左右的块。2.热锅放入玉米油，放入鸡胸块、洋葱、土豆和胡萝卜翻炒至鸡肉变色，放入料酒烹香。3.加入400g水中火烧制10分钟左右，至土豆和鸡肉变得软烂。4.转小火，放入咖喱块，搅拌至完全融化，再炖煮5分钟左右至咖喱呈稠糊状鸡肉入味即可。', '/articleimages/img20.png', '1', '7');
insert into menu_article values('21', '蘑菇炒鸡肉', '主料：蘑菇150克，鸡肉100克 辅料：料酒4克，盐2克，生抽4克，鸡精1克', '1.鸡肉切成条。2.然后加料酒，生抽淀粉抓匀腌15分钟。3.蘑菇洗干净切成片。4.锅里倒入油倒入腌好的鸡肉炒匀。5.快速炒散，炒至鸡肉白。6.然后加入蘑菇翻炒。7.加入盐炒匀，炒至蘑菇软。8.加入生抽大火不停的翻炒。9.炒至入味后加点鸡精炒会即可。10.炒好装盘。', '/articleimages/img21.png', '2', '7');
insert into menu_article values('22', '西兰花炒虾仁', '主料：西兰花300克，虾仁适量 辅料：姜片3克，料酒3毫升，食用油5毫升，鸡粉2克盐，2克水，淀粉5克', '1.准备好食材。2.准备姜片，西蓝花。3.加入食用油，姜片，大火，虾仁，煸炒。4.锅中倒入料酒，西蓝花，翻炒。5.加入鸡粉，盐，翻炒。6.倒入水淀粉，翻炒即可。\r', '/rticleimages/img22.png', '3', '3');
insert into menu_article values('23', '豆角炖排骨', '主料：排骨500克，豆角50克 辅料：生姜5克，大葱10克，蒜末5克，食用油5毫升，八角2克，生抽3克，料酒5克，水500毫升', '1.排骨洗干净砍成小块，冷水下锅，放姜片、香葱段、部分料酒煮出血水捞出控干。2.豆角择取筋，清洗干净，切成寸段，备用。3.锅里放油烧至五成热，放入姜片爆香后，将焯好的排骨倒入锅中炒匀。4.向锅中加入酱油、料酒、白糖炒匀后，倒入清水没过排骨。5.锅中加入家乐猪骨汤口味浓汤宝，煮沸。6.将豆角倒入锅中，大火煮开后，小火焖20分钟左右。7.打开锅盖，继续用大火收汁至浓稠，汤汁有层层拉丝的感觉；将收好汁的排骨炖豆角倒入盘中即可。', '/articleimages/img23.png', '3', '1');
insert into menu_article values('24', '猪肚鸡', '主料：猪肚1整个，三黄鸡1整只，生花生1把，纯牛奶100毫升 辅料：香叶、八角、大葱、姜片、枸杞3片，花椒2撮，桂皮1小截，白胡椒粉1调料勺', '1.准备好食材。2.鸡腿1个，冷水下锅，加入料酒5克、姜片8克。3.焯水去腥去血沫。4.将清水加入锅中，放入鸡腿，猪肚条150克、红枣2个、当归3片、党参2克、黄芪2克、枸杞2克，搅拌均匀。5.继续加入清水，白胡椒粉0.5克，花椒粉0.5克，小火煲2小时。6.加入纯牛奶，继续煲15分钟即可出锅。', '/articleimages/img24.png', '1', '7');
insert into menu_article values('25', '芹菜炒鸡丝', '主料：鸡肉120克，芹菜130克 辅料：葱1根，姜2片，蒜2瓣，番茄1/2个，盐1/3勺，酱油1/2勺，生粉1/3勺，鸡粉1/4勺', '1.准备食材：青菜斜切小段。2.水焯一下芹菜。3.鸡肉切丝，调入料酒、胡椒粉、盐、生粉腌制。4.热锅冷油，放入鸡丝快速滑炒。5.炒至鸡丝变色出锅备用。6.底油，放入葱、姜、蒜、花椒粉、番茄块，炒出香味。7.放入芹菜翻炒。8.放入鸡丝翻炒。9.调入酱油、盐、鸡粉翻炒出锅。', '/articleimages/img25.png', '1', '1');
insert into menu_article values('26', '清蒸鲈鱼', '主料：鲈鱼1条 辅料：大葱白1根，姜适量，大蒜2瓣，白糖1茶匙，料酒1大勺，香油适量，植物油2大勺，酱油2勺', '1.鲈鱼清洗干净，用厨房纸吸去水份。2.两面花刀，秣上细盐，肚子里面也要抹上。淋上蒸鱼油抹均匀。3.葱切段姜切片。4.摆在盘子里面。5.放上腌好的鱼，鱼肚中可以放上葱和姜。6.水开后上锅，蒸10分钟。虚蒸三分钟。7.出锅。将鱼换一下盘子，或者将汤汁倒出来。8.将汤汁慢慢的烧在鱼身上。9.另起一小锅，将一勺油烧热，浇在鱼身上。放上藿香，上桌就可以了。', '/articleimages/img26.png', '3', '3');
insert into menu_article values('27', '小鸡炖蘑菇', '主料:鸡半只,干磨菇50克 辅料:料酒1汤勺,老抽1汤匙,桂皮1小块,八角3个,盐1小勺,食用油2汤匙,大蒜1根,姜3片', '1.将三黄鸡斩件，泡净血水备用；干香菇用温水泡发，洗净备用。2.三黄鸡放入水中，大火烧开，撇去浮沫后捞出，洗净备用。3.锅中放油烧热，煸香葱段、姜块、八角、香叶，然后将鸡放入煸炒至变色，加入料酒、酱油翻炒均匀。4.加入清水，放入干香菇炖煮，加入家乐浓汤宝炆炖专用。5.家乐浓汤宝炆炖专用溶成浓醇高汤，渗入食材，大火烧开后转小火炖至汤汁收浓，最后撒白胡椒粉提香即可。', '/articleimages/img27.png', '1', '7');
#分类表
create table menu_category(
  id smallint unsigned NOT NULL AUTO_INCREMENT  COMMENT '分类id，主键且非空',
  category_name varchar(8) NOT NULL,
  PRIMARY KEY(`id`),
  UNIQUE KEY `category_name` (`category_name`)
);
insert into menu_category values('1','meat'),('2','dumplings'),('3','fish'),('4','soup'),('5','code'),('6','vegetabe'),('7','poultry');
